var minionName = "Blizzaria";
var minionObject = GameObjectManager.GetObjectByObjectId(1);
if (minionObject != null)
{
	if (minionObject.EnglishName.Equals(minionName))
	{
		Logging.Write("[Yo-kai Watch] " + minionName + " is already summoned!");
	}
	else
	{
		Logging.Write("[Yo-kai Watch] The wrong minion is currently active! Assuming " + minionName + " is not summoned.");
		await Buddy.Coroutines.Coroutine.Sleep(1000);
		Logging.Write("[Yo-kai Watch] Summoning " + minionName + " now...");
		await Buddy.Coroutines.Coroutine.Sleep(500);
		ff14bot.Managers.ChatManager.SendChat("/minion Blizzaria");
		await Buddy.Coroutines.Coroutine.Sleep(3000);
	}
}
else
{
	Logging.Write("[Yo-kai Watch] You do not have an active minion. Assuming " + minionName + " is not summoned.");
	await Buddy.Coroutines.Coroutine.Sleep(1000);
	Logging.Write("[Yo-kai Watch] Summoning " + minionName + " now...");
	await Buddy.Coroutines.Coroutine.Sleep(500);
	ff14bot.Managers.ChatManager.SendChat("/minion Blizzaria");
